class Solution:
    def maximumScore(self, nums: List[int], k: int) -> int:
        score = 1
        chosen = set()

        for _ in range(k):
            best = 1
            best_idx = -1

            for i in range(len(nums)):
                for j in range(i+1, len(nums)+1):
                    if (i, j) not in chosen:
                        cur = max(nums[i:j])
                        cur_score = len(set(numpy.primefactors(cur)))
                        if cur_score > best:
                            best = cur_score 
                            best_idx = cur

            chosen.add((i, j))
            score = score * best % (10**9 + 7)

        return score

