                    elif num % 10 == 9:
                        cur.append(num*10 + 8)
                    else:
                        cur.append(num*10 + num%10 - 1)
                        cur.append(num*10 + num%10 + 1)
                res.extend(cur)
            return res

        nums = get_stepping_numbers(high)
        return sum(1 for num in nums if low <= num <= high) % (10**9 + 7)
