class Solution:
    def countPalindromePaths(self, parent: List[int], s: str) -> int:
        n = len(parent)
        graph = defaultdict(list)
        for i in range(1, n):
            graph[parent[i]].append(i)

        def dfs(node, path):
            cnt = 0
            for child in graph[node]:
                new_path = path + s[child]
                if new_path == new_path[::-1]:
                    cnt += 1
                cnt += dfs(child, new_path)
            return cnt

        return dfs(0, '') + n - 1

