class Solution:
    def maxOutput(self, n: int, edges: List[List[int]], price: List[int]) -> int:
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(node, parent):
            nonlocal max_path, min_path
            curr_sum = price[node]
            max_path = max(max_path, curr_sum)
            min_path = min(min_path, curr_sum)

            for child in graph[node]:
                if child != parent:
                    dfs(child, node)
                    max_path = max(max_path, curr_sum + max_path)
                    min_path = min(min_path, curr_sum + min_path)

        max_diff = 0
        for i in range(n):
            max_path = 0
            min_path = float('inf')
            dfs(i, -1)
            max_diff = max(max_diff, max_path - min_path)

        return max_diff

