            grid[r][c] = 0

            left = dfs(r, c-1)
            right = dfs(r, c+1) 
            up = dfs(r-1, c)
            down = dfs(r+1, c)

            grid[r][c] = fish
            return fish + max(left, right, up, down)

        return max(dfs(r, c) for r in range(m) for c in range(n))

