class Solution:
    def smallestValue(self, n: int) -> int:
        def prime_factors(n):
            i = 2
            factors = []
            while i * i <= n:
                if n % i == 0:
                    factors.append(i)
                    n //= i
                else:
                    i += 1
            if n > 1: 
                factors.append(n)
            return factors

        while n > 1:
            factors = prime_factors(n)
            n = sum(factors)
        return n

