class Solution:
    def minTimeToVisitAllPoints(self, points: List[List[int]]) -> int:
        time = 0
        for i in range(1,len(points)):
            prev = points[i-1]
            curr = points[i]
            x_diff = abs(curr[0] - prev[0])
            y_diff = abs(curr[1] - prev[1])
            time += max(x_diff, y_diff)
        return time

