completion:
class Solution:
    def numPrimeArrangements(self, n: int) -> int:
        primes = [True] * (n+1)
        primes[0] = primes[1] = False
        for i in range(2, int(n**0.5)+1):
            if primes[i]:
                for j in range(i*i, n+1, i):
                    primes[j] = False

        num_primes = sum(primes[2:])

        MOD = 10**9 + 7
        return factorial(num_primes) * factorial(n - num_primes) % MOD

def factorial(n):
    result = 1
    for i in range(2, n+1):
        result = result * i % MOD
    return result

