from typing import List

class Solution:
    def findCrossingTime(self, n: int, k: int, time: List[List[int]]) -> int:
        def is_less_efficient(i, j):
            return time[i][0] + time[i][2] > time[j][0] + time[j][2] or (time[i][0] + time[i][2] == time[j][0] + time[j][2] and i > j)

        def find_min_index(workers):
            min_index = min(workers, key=lambda x: time[x][1] + time[x][3])
            workers.remove(min_index)
            return min_index

        def find_min_efficiency(workers):
            min_efficiency = min(workers, key=lambda x: time[x][1] + time[x][3] if not is_less_efficient(x, time[x][1] + time[x][3]) else float('inf'))
            workers.remove(min_efficiency)
            return min_efficiency

        workers_left = [i for i in range(k)]
        workers_right = []
        time_elapsed = 0

        while workers_left or workers_right:
            if workers_right:
                worker = workers_right.pop(0)
                time_elapsed += time[worker][2]
            elif workers_left:
                if not workers_right:
                    worker = find_min_efficiency(workers_left)
                else:
                    worker = find_min_index(workers_left)
                time_elapsed += time[worker][0]
                workers_right.append(worker)
            else:
                break

            n -= 1
            if not n:
                return time_elapsed

            time_elapsed += time[worker][1] + time[worker][3]

        return time_elapsed
