from typing import List

class Solution:
    def maxPower(self, stations: List[int], r: int, k: int) -> int:
        n = len(stations)
        stations.sort()

        for i in range(n - 1):
            if stations[i + 1] - stations[i] <= r:
                stations[i + 1] = stations[i]

        power = [0] * n
        for i in range(n):
            left, right = i - r, i + r
            if left < 0:
                left = 0
            if right >= n:
                right = n - 1
            power[i] = stations[right] - stations[left] + 1

        power.sort(reverse=True)
        for i in range(k):
            if power[i] > power[k]:
                power[k] = power[i]

        return power[k]

# Test cases
