from typing import List

def is_prime(n: int) -> bool:
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

class Solution:
    def closestPrimes(self, left: int, right: int) -> List[int]:
        if left == 1:
            left = 2
        primes = []
        for i in range(left, right + 1):
            if is_prime(i):
                primes.append(i)
        if not primes:
            return [-1, -1]
        min_diff = float('inf')
        result = [0, 0]
        for i in range(len(primes) - 1):
            diff = primes[i + 1] - primes[i]
            if diff < min_diff:
                min_diff = diff
                result = [primes[i], primes[i + 1]]
        return result
