class Solution:
    def countAnagrams(self, s: str) -> int:
        MOD = 10**9 + 7
        from collections import Counter

        def count_anagrams(s, start, end):
            if start >= end:
                return 1
            if s[start] == s[end]:
                return (count_anagrams(s, start + 1, end) + count_anagrams(s, start, end - 1)) % MOD
            else:
                return count_anagrams(s, start + 1, end) + count_anagrams(s, start, end - 1)

        words = s.split()
        total_anagrams = 1
        for i in range(len(words)):
            word = words[i]
            freq = Counter(word)
            divisor = 1
            for value in freq.values():
                divisor *= factorial(value)
            total_anagrams *= count_anagrams(word, 0, len(word) - 1) // divisor
            total_anagrams %= MOD

        return total_anagrams

    def factorial(self, n):
        res = 1
        for i in range(1, n + 1):
            res *= i
        return res

