from typing import List

class Solution:
    def captureForts(self, forts: List[int]) -> int:
        n = len(forts)
        dp = [0] * n
        for i in range(n - 1, -1, -1):
            if forts[i] == 1:
                left, right = i, i
                while left > 0 and forts[left - 1] == 0:
                    left -= 1
                while right < n - 1 and forts[right + 1] == 0:
                    right += 1
                dp[i] = max(dp[k] for k in range(left, right + 1)) + (right - left + 1)
        return max(dp)

