from typing import List

class Solution:
    def cycleLengthQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        def find_parent(node):
            if parent[node] != node:
                parent[node] = find_parent(parent[node])
            return parent[node]

        def union(node1, node2):
            parent1, parent2 = find_parent(node1), find_parent(node2)
            if parent1 != parent2:
                parent[parent1] = parent2
                return True
            return False

        parent = list(range(2**n - 1))
        for i in range(2**n - 2, 0, -1):
            if i % 2 == 0:
                union(i, i + 1)

        answer = []
        for a, b in queries:
            if union(a, b):
                length = 1
                while find_parent(a) != find_parent(b):
                    length += 1
                    a = find_parent(a)
                    b = find_parent(b)
                answer.append(length)
            else:
                answer.append(0)
            union(a, b)

        return answer

