from typing import List

class Solution:
    def isPossible(self, n: int, edges: List[List[int]]) -> bool:
        def dfs(node, parent, visited, degrees):
            if visited[node]:
                return False
            visited[node] = True
            for neighbor in graph[node]:
                if neighbor != parent:
                    if not dfs(neighbor, node, visited, degrees):
                        return False
                    degrees[node] += degrees[neighbor]
            degrees[node] -= n - 2
            return True

        graph = [[] for _ in range(n)]
        for a, b in edges:
            graph[a - 1].append(b - 1)
            graph[b - 1].append(a - 1)

        degrees = [0] * n
        visited = [False] * n
        for i in range(n):
            if not dfs(i, -1, visited, degrees):
                return False

        return all(degree % 2 == 0 for degree in degrees)
