class Solution:
    def maxPoints(self, grid: List[List[int]], queries: List[int]) -> List[int]:
        def dfs(x, y, query):
            if x < 0 or x >= len(grid) or y < 0 or y >= len(grid[0]) or grid[x][y] > query:
                return 0
            if not visited[x][y]:
                visited[x][y] = True
                return 1 + max(dfs(x - 1, y, query), dfs(x + 1, y, query), dfs(x, y - 1, query), dfs(x, y + 1, query))
            return 0

        result = []
        for query in queries:
            visited = [[False] * len(grid[0]) for _ in range(len(grid))]
            result.append(dfs(0, 0, query))
        return result
