            groups[node] = group
            for neighbor in graph[node]:
                if not vis[neighbor]:
                    if not dfs(neighbor, -group):
                        return False
            return True

        graph = [[] for _ in range(n + 1)]
        for a, b in edges:
            graph[a].append(b)
            graph[b].append(a)

        for i in range(1, n + 1):
            if not vis[i]:
                if not dfs(i, 1):
                    return -1

        m = max(groups)
