from typing import List
import numpy as np

class Solution:
    def dividePlayers(self, skill: List[int]) -> int:
        n = len(skill)
        skill = np.array(skill)
        skill.sort()

        total_skill = skill.sum()
        if total_skill % 2 != 0:
            return -1

        target_skill = total_skill // 2
        left, right = 0, n - 1
        teams = []

        while left < right:
            team_skill = skill[left] + skill[right]
            if team_skill == target_skill:
                teams.append(skill[left] * skill[right])
                left += 1
                right -= 1
            elif team_skill < target_skill:
                left += 1
            else:
                right -= 1

        return sum(teams)

