            dp[i][int(s[i])] += 1

        ans = 0
        for i in range(n):
            for j in range(i+1, n):
                if j-i+1 == 5:
                    for k in range(10):
                        if dp[j][k] > dp[i][k]:
                            ans += dp[j][k] - dp[i][k]
                    ans += 1
                elif j-i+1 > 5:
                    for k in range(1, 10):
                        if dp[j][k] - dp[i][k] > 0:
                            ans += dp[j][k] - dp[i][k]
                            if k == 9:
                                ans += 1
                        else:
