class Solution:
    def bestClosingTime(self, customers: str) -> int:
        n = len(customers)
        no_customers = customers.count('N')
        customers_before = [0] * n
        customers_after = [0] * n

        for i in range(1, n):
            customers_before[i] = customers_before[i - 1] + (customers[i - 1] == 'N')

        for i in range(n - 2, -1, -1):
            customers_after[i] = customers_after[i + 1] + (customers[i + 1] == 'N')

        min_penalty = float('inf')
        min_hour = -1

        for i in range(n):
            penalty = customers_before[i] + customers_after[i] - no_customers + (customers[i] == 'N')
            if penalty < min_penalty:
                min_penalty = penalty
                min_hour = i

        return min_hour

