from typing import List

class Solution:
    def distinctAverages(self, nums: List[int]) -> int:
        nums.sort()
        averages = set()

        while nums:
            min_num = nums.pop(0)
            max_num = nums.pop(-1)
            average = (min_num + max_num) / 2
            averages.add(average)

        return len(averages)

