from typing import List

class Solution:
    def subarrayGCD(self, nums: List[int], k: int) -> int:
        def gcd(a, b):
            while b:
                a, b = b, a % b
            return a

        def lcm(a, b):
            return a * b // gcd(a, b)

        n = len(nums)
        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i + 1] = lcm(prefix[i], nums[i])

        suffix = [0] * (n + 1)
        for i in range(n - 1, -1, -1):
            suffix[i] = lcm(suffix[i + 1], nums[i])

        count = 0
        for i in range(n):
            if gcd(prefix[i], suffix[i + 1]) == k:
                count += 1

        return count

