from typing import List

class Solution:
    def numberOfPaths(self, grid: List[List[int]], k: int) -> int:
        m, n = len(grid), len(grid[0])
        mod = 10**9 + 7
        dp = [[0] * n for _ in range(m)]
        dp[0][0] = 1

        for i in range(m):
            for j in range(n):
                if i > 0:
                    dp[i][j] += dp[i - 1][j]
                if j > 0:
                    dp[i][j] += dp[i][j - 1]
                dp[i][j] %= mod

        res = 0
        for i in range(m):
            for j in range(n):
                if (sum(grid[i][:j + 1]) + sum(grid[:i + 1])) % k == 0:
                    res += dp[i][j]
                    res %= mod

        return res

