from typing import List

class Solution:
    def hardestWorker(self, n: int, logs: List[List[int]]) -> int:
        tasks = [0] * n
        for i in range(len(logs)):
            tasks[logs[i][0]] = logs[i][1] - (logs[i - 1][1] if i > 0 else 0)

        max_time = max(tasks)
        min_id = min(i for i, time in enumerate(tasks) if time == max_time)

        return min_id

