completion:
class Solution:
    def countAnagrams(self, s: str) -> int:
        MOD = 10**9 + 7
        N = len(s)
        factorial = [1]*(N+1)
        for i in range(1, N+1):
            factorial[i] = factorial[i-1]*i % MOD
        prefix_hash = [0]*(N+1)
        for i in range(1, N+1):
            prefix_hash[i] = (prefix_hash[i-1]*26 + ord(s[i-1]) - ord('a') + 1) % MOD
        anagram_count = [0]*(N+1)
        anagram_count[0] = 1
        for i in range(1, N+1):
            anagram_count[i] = (anagram_count[i-1]*2 + factorial[2*i-1]*factorial[2*i] % MOD*prefix_hash[i]**(N-i+1)) % MOD
        return anagram_count[N]

