class Solution:
    def countPartitions(self, nums: List[int], k: int) -> int:
        mod = 10**9 + 7
        n = len(nums)
        total_sum = sum(nums)
        if total_sum < k:
            return 0
        dp = [[0]*(total_sum+1) for _ in range(n+1)]
        dp[0][0] = 1
        for i in range(1, n+1):
            dp[i][0] = 1
            for j in range(1, total_sum+1):
                dp[i][j] = (dp[i-1][j] + dp[i][j-nums[i-1]]) % mod
        return dp[n][total_sum]

