class Solution:
    def takeCharacters(self, s: str, k: int) -> int:
        from collections import Counter
        cnt = Counter(s)
        left, right = 0, 0
        for c in s:
            cnt[c] -= 1
            while all(v >= k for v in cnt.values()):
                cnt[s[left]] += 1
                left += 1
            right += 1
        return right if right >= k else -1

