class Solution:
    def maxPoints(self, grid: List[List[int]], queries: List[int]) -> List[int]:
        m, n = len(grid), len(grid[0])
        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]

        def dfs(x, y, q):
            visited = [[0] * n for _ in range(m)]
            stack = [(x, y, 1)]
            points = 1
            while stack:
                nx, ny, np = stack.pop()
                if np > points:
                    points = np
                for dx, dy in directions:
                    nnx, nny = nx + dx, ny + dy
                    if 0 <= nnx < m and 0 <= nny < n and not visited[nnx][nny] and grid[nnx][nny] < q:
                        visited[nnx][nny] = 1
                        stack.append((nnx, nny, np + 1))
            return points

        return [dfs(i // n, i % n, q) for i, q in enumerate(queries)]

