class Solution:
    def magnificentSets(self, n: int, edges: List[List[int]]) -> int:
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)
        visited = [0] * (n + 1)
        groups = [0]
        for i in range(1, n + 1):
            if not visited[i]:
                visited[i] = 1
                queue = [i]
                while queue:
                    node = queue.pop(0)
                    for neighbor in graph[node]:
                        if not visited[neighbor]:
                            visited[neighbor] = 1
                            queue.append(neighbor)
                groups.append(0)
