from typing import List

class Solution:
    def dividePlayers(self, skill: List[int]) -> int:
        n = len(skill)
        skill.sort(reverse=True)
        total = sum(skill)
        if total % 2 != 0:
            return -1
        target = total // 2
        dp = [0] * (target + 1)
        dp[0] = 1
        for num in skill:
            for i in range(target, num - 1, -1):
                dp[i] |= dp[i - num]
            if dp[target]:
