# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def closestNodes(self, root: Optional[TreeNode], queries: List[int]) -> List[List[int]]:
        def inorder(node, arr):
            if node:
                inorder(node.left, arr)
                arr.append(node.val)
                inorder(node.right, arr)

        def find_closest(arr, target):
            left, right = -1, -1
            for val in arr:
                if val <= target:
                    left = max(left, val)
                elif val >= target:
                    right = min(right, val) if right != -1 else val
            return [left, right]

        tree_values = []
        inorder(root, tree_values)
        tree_values = sorted(tree_values)

        return [find_closest(tree_values, q) for q in queries]

