        time_elapsed = 0
        boxes_left = n
        while boxes_left > 0:
            while len(workers) > 0 and workers[0][0] <= time_elapsed:
                _, leftToRight, pickOld, rightToLeft, putNew, worker_id = heapq.heappop(workers)
                time_elapsed += leftToRight
