completion:
class Solution:
    def minimizeSet(self, divisor1: int, divisor2: int, uniqueCnt1: int, uniqueCnt2: int) -> int:
        # Find the least common multiple (LCM) of divisor1 and divisor2
        lcm = divisor1 * divisor2 // math.gcd(divisor1, divisor2)

        # Calculate the maximum possible value for arr1 and arr2
        max_val1 = (uniqueCnt1 * lcm) // divisor1
        max_val2 = (uniqueCnt2 * lcm) // divisor2

        # Return the minimum possible maximum value
        return min(max_val1, max_val2)

