completion:
class Solution:
    def maxPower(self, stations: List[int], r: int, k: int) -> int:
        n = len(stations)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + stations[i]

        max_power = 0
        for i in range(n):
            left = max(0, i - r)
            right = min(n, i + r + 1)
            total_power = prefix_sum[right] - prefix_sum[left]
            max_power = max(max_power, total_power)

        if k >= n:
            return max_power

        for i in range(n):
            if stations[i] == 0:
                stations[i] = 1
                break

        for _ in range(k):
            max_power = max(max_power, max(stations))
            idx = stations.index(max(stations))
            stations[idx] += 1

        return max_power

