        if len(primes) < 2:
            return [-1, -1]
        else:
            min_diff = float('inf')
            min_pair = None
            for i in range(len(primes) - 1):
                if primes[i + 1] - primes[i] < min_diff:
                    min_diff = primes[i + 1] - primes[i]
                    min_pair = (primes[i], primes[i + 1])
            return list(min_pair)

