class Solution:
    def cycleLengthQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        parent = [i for i in range(2**n)]
        depth = [0 for _ in range(2**n)]

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            root_x = find(x)
            root_y = find(y)
            if root_x != root_y:
                if depth[root_x] < depth[root_y]:
                    parent[root_x] = root_y
                elif depth[root_x] > depth[root_y]:
                    parent[root_y] = root_x
                else:
                    parent[root_y] = root_x
                    depth[root_x] += 1

        for i in range(2**n - 1, 0, -1):
            union(i, i//2)

        ans = []
        for a, b in queries:
            if find(a) == find(b):
                ans.append(depth[find(a)] + depth[find(b)])
            else:
                union(a, b)
                ans.append(depth[find(a)] + depth[find(b)] - 1)
                parent[find(a)] = a
                parent[find(b)] = b
                depth[find(a)] = 0
                depth[find(b)] = 0
        return ans

