from collections import defaultdict

class Solution:
    def magnificentSets(self, n: int, edges: List[List[int]]) -> int:
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)
        visited = [0] * (n + 1)
        groups = [0]
        for node in range(1, n + 1):
            if visited[node]:
                continue
            group = 0
            stack = [node]
            while stack:
                node = stack.pop()
                if visited[node]:
                    continue
                visited[node] = 1
                group ^= node
                stack.extend(graph[node])
            if group not in groups:
                groups.append(group)
        return len(groups) if len(groups) == n else -1

