import heapq

class Solution:
    def minScore(self, n: int, roads: List[List[int]]) -> int:
        graph = [[] for _ in range(n+1)]
        for u, v, w in roads:
            graph[u].append((v, w))
            graph[v].append((u, w))
        dist = [float('inf')] * (n+1)
        dist[1] = 0
        heap = [(0, 1)]
        while heap:
            d, node = heapq.heappop(heap)
            if d != dist[node]:
                continue
            for nei, nei_dist in graph[node]:
                if dist[node] + nei_dist < dist[nei]:
                    dist[nei] = dist[node] + nei_dist
                    heapq.heappush(heap, (dist[nei], nei))
        return min(dist[1:])
```

