        count = [[0]*alphabet_size for _ in range(str_length)]

        # Fill the count array
        for i in range(str_length):
            for j in range(i+1, str_length+1):
                count[i][int(s[j-1])] += 1

        # Function to calculate palindrome count
        def palindrome_count_util(start: int, end: int) -> int:
            nonlocal palindrome_count
            if start >= end:
                return 0
            temp = [0]*alphabet_size
            for i in range(alphabet_size):
                temp[i] = count[start][i]
            for i in range(alphabet_size):
                count[start][i] -= temp[i]
            for i in range(alphabet_size):
                palindrome_count = (palindrome_count + temp[i]*(temp[i]-1)//2) % MOD
            for i in range(start+1, end):
