class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        left = 0
        right = 0
        while right < n:
            if nums[right] < k:
                right += 1
            else:
                left = right
                while right < n and nums[right] >= k:
                    right += 1
                count += (right - left) * (left - 1) + (right - left) * (n - right)
        return count

