import heapq

class Solution:
    def minimumFuelCost(self, roads: List[List[int]], seats: int) -> int:
        graph = defaultdict(list)
        for u, v, w in roads:
            graph[u].append((v, w))
            graph[v].append((u, w))
        pq = [(0, 0)]  # (cost, city)
        visited = [False] * len(graph)
        total_cost = 0
        while pq:
            cost, city = heapq.heappop(pq)
            if not visited[city]:
                visited[city] = True
                total_cost += cost
                if len(visited) == len(graph):
                    break
                for nei, nei_cost in graph[city]:
                    if not visited[nei]:
                        heapq.heappush(pq, (nei_cost, nei))
