from collections import defaultdict

class Solution:
    def componentValue(self, nums: List[int], edges: List[List[int]]) -> int:
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(node, parent):
            subtree_sum = nums[node]
            children_count = 1
            for child in graph[node]:
                if child != parent:
                    child_sum, child_count = dfs(child, node)
                    subtree_sum += child_sum
                    children_count += child_count
            return subtree_sum, children_count

        total_sum, total_count = dfs(0, -1)
        target_value = total_sum // total_count

        def check(edges_to_delete):
            remaining_edges = set(range(len(edges))) - set(edges_to_delete)
            visited = [False] * len(nums)
            stack = [0]
            while stack:
                node = stack.pop()
                if not visited[node]:
                    subtree_sum = nums[node]
                    children_count = 1
                    for child in graph[node]:
                        if not visited[child]:
                            stack.append(child)
                            children_count += 1
                    if subtree_sum + target_value * children_count == nums[node]:
                        return True
                    visited[node] = True
            return False

        left, right = 0, len(edges)
        while left < right:
            mid = (left + right) // 2
            if check(list(range(len(edges)))[:mid]):
                left = mid + 1
            else:
                right = mid
        return len(edges) - left

