                if i > 0:
                    dp[i][j] += dp[i-1][j]
                if j > 0:
                    dp[i][j] += dp[i][j-1]
                dp[i][j] %= mod

        return dp[m-1][n-1] * pow(k, m + n - 2, mod) % mod

