class DataStream:

    def __init__(self, value: int, k: int):
        self.value = value
        self.k = k
        self.arr = []

    def consec(self, num: int) -> bool:
        self.arr.append(num)
        if len(self.arr) < self.k:
            return False
        if len(self.arr) > self.k:
            self.arr.pop(0)
        return self.arr[-1] == self.value and self.arr[0] == self.value and len(set(self.arr)) == 1

