class Solution:
    def maxPower(self, stations: List[int], r: int, k: int) -> int:
        stations.sort()
        max_power = 0
        for i in range(len(stations)):
            max_power = max(max_power, stations[i] + sum(stations[j] for j in range(i+1, min(i+r+1, len(stations)))))
        return max_power

