class Solution:
    def countAnagrams(self, s: str) -> int:
        MOD = 10**9 + 7
        n = len(s)
        dp = [0] * (n+1)
        dp[0] = 1
        for i in range(1, n+1):
            dp[i] = (dp[i-1] * 26) % MOD
            if s[i-1] == s[i-2]:
                dp[i] = (dp[i] + dp[i-2]) % MOD
        return dp[n]

