class Solution:
    def captureForts(self, forts: List[int]) -> int:
        # check if there are any forts under your command
        if 1 not in forts:
            return 0
        # find the index of the first fort under your command
        start = forts.index(1)
        # find the index of the last fort under your command
        end = len(forts) - 1 - forts[::-1].index(1)
        # initialize the max number of forts that can be captured
        max_capture = 0
        # loop through all the forts under your command
        for i in range(start, end + 1):
            # if the fort is under your command
            if forts[i] == 1:
                # initialize the number of forts that can be captured
                capture = 0
                # loop through all the forts from the current fort to the end
                for j in range(i, end + 1):
                    # if the fort is an enemy fort
                    if forts[j] == 0:
                        # increment the number of forts that can be captured
                        capture += 1
                # if the number of forts that can be captured is greater than the current max
                if capture > max_capture:
                    # update the max number of forts that can be captured
                    max_capture = capture
        # return the max number of forts that can be captured
        return max_capture

