class Solution:
    def isPossible(self, n: int, edges: List[List[int]]) -> bool:
        # build adjacency list
        adj = [[] for _ in range(n)]
        for u, v in edges:
            adj[u-1].append(v-1)
            adj[v-1].append(u-1)

        # find a cycle
        visited = [False] * n
        def has_cycle(u):
            if visited[u]:
                return True
            visited[u] = True
            for v in adj[u]:
                if visited[v] or has_cycle(v):
                    return True
            visited[u] = False
            return False

        # check if there is a cycle
        for u in range(n):
            if has_cycle(u):
                return False

        # check if all nodes have even degree
        return all(len(adj[u]) % 2 == 0 for u in range(n))

