class Solution:
    def maxJump(self, stones: List[int]) -> int:
        n = len(stones)
        dp = [0] * n
        for i in range(1, n):
            for j in range(i):
                if stones[i] - stones[j] > j - i:
                    dp[i] = max(dp[i], dp[j])
                else:
                    dp[i] = max(dp[i], dp[j] + stones[i] - stones[j])
        return dp[-1]

