completion:
class Solution:
    def maxStarSum(self, vals: List[int], edges: List[List[int]], k: int) -> int:
        # adjacency list
        adj = [[] for _ in vals]
        for a, b in edges:
            adj[a].append(b)
            adj[b].append(a)

        # find the center of the star graph
        # the center has the most edges
        center = 0
        max_edges = 0
        for i in range(len(vals)):
            if len(adj[i]) > max_edges:
                center = i
                max_edges = len(adj[i])

        # find the maximum sum of the star graph
        # the sum is the sum of the center and the k largest neighbors
        # of the center
        neighbors = sorted(adj[center], key=lambda x: vals[x])
        return vals[center] + sum(vals[i] for i in neighbors[-k:])

